insert into [tbl_ArticleInfo]([ImportID],[Title],[ImportCatalog],[Note],[ImportPublicDate],[SerialNum]) select '698','市直机关工会工委组织市直机关代表队参加市体育局举办的全市保龄球邀请赛','大事记','<p class="p1"><span class="s1">6</span><span class="s2">月</span><span class="s1">6</span><span class="s2">日至</span><span class="s1">7</span><span class="s2">日，市直机关工会工委组织市直机关代表队参加市体育局举办的全市保龄球邀请赛。市直机关代表队分别参加女子团体、男子团体、女子个人、男子个人赛，团体比赛获得女子团体第一名、男子团体第四名，个人比赛获得两个女子个人第一名的好成绩。</span></p>','2017/07/03 12:21:21','0';
