insert into [tbl_ArticleInfo]([ImportID],[Title],[ImportCatalog],[Note],[ImportPublicDate],[SerialNum]) select '692','“弘扬中华文化、践行社会主义核心价值观”的专题报告','大事记','<p class="p1"><span class="s1">4</span><span class="s2">月</span><span class="s1">17</span><span class="s2">日，为认真贯彻落实中共中央办公厅《关于培育和践行社会主义核心价值观意见》精神和市委十一届党代会第二次会议</span><span class="s1">“</span><span class="s2">弘扬和践行社会主义核心价值观</span><span class="s1">”</span><span class="s2">精神，市直机关工会工委邀请世界杰出华人、人民艺术家协会副会长籍更森教授在市政协委员讲堂给机关干部职工作了一场题为</span><span class="s1">“</span><span class="s2">弘扬中华文化、践行社会主义核心价值观</span><span class="s1">”</span><span class="s2">的专题报告，市直机关干部</span><span class="s1">400</span><span class="s2">多人参加。籍教授的讲课生动活泼、内容丰富，进一步帮助机关干部职工深刻了解了</span><span class="s1">“</span><span class="s2">富强、民主、文明、和谐，自由、平等、公正、法治，爱国、敬业、诚信、友善</span><span class="s1">”</span><span class="s2">社会主义核心价值观的丰富内涵和实践要求，增强了机关干部职工践行社会主义核心价值观的自觉性和主动性。</span></p>','2017/07/03 12:19:00','0';
insert into [tbl_ArticleInfo]([ImportID],[Title],[ImportCatalog],[Note],[ImportPublicDate],[SerialNum]) select '690','市直机关工会工委在市总工会礼堂举办市直机关干部职工“健康知识”讲座','大事记','<p class="p1"><span class="s1">3</span><span class="s2">月</span><span class="s1">10</span><span class="s2">日，市直机关工会工委在市总工会礼堂举办市直机关干部职工</span><span class="s1">“</span><span class="s2">健康知识</span><span class="s1">”</span><span class="s2">讲座，来自</span><span class="s1">73</span><span class="s2">个单位的</span><span class="s1">451</span><span class="s2">名干部职工参加。中国新九针广东培训基地中心主任邢伟传讲解春季天气特点和春季健康知识</span><span class="s1">;</span><span class="s2">广州汉唐教育副总裁、华夏文化书院研究员陈解涛主讲《生活中的化学》，讨论我们生活中的毒素来源，解析当前饮食医药中的化学物质对人体健康的影响。</span></p>','2017/07/03 12:17:11','0';
insert into [tbl_ArticleInfo]([ImportID],[Title],[ImportCatalog],[Note],[ImportPublicDate],[SerialNum]) select '688','市直机关工会在市委党校举办2015年市直机关第一期工会干部培训班','大事记','<p class="p1"><span class="s1">2</span><span class="s2">月</span><span class="s1">12</span><span class="s2">日至</span><span class="s1">13</span><span class="s2">日，市直机关工会在市委党校举办</span><span class="s1">2015</span><span class="s2">年市直机关第一期工会干部培训班，培训为期两天，</span><span class="s1">108</span><span class="s2">名机关工会干部参加。此次培训着重解读市直机关工会</span><span class="s1">2015</span><span class="s2">年工作要点及安排；邀请自治区总工会《广西工人日报》社长助理张传韬就如何做好新形势下机关工会宣传报道工作进行培训；邀请市总工会经审办主任李国勇就如何规范使用工会经费进行讲解；传达学习自治区党委书记彭清华在区总工会第十二次代表大会开幕式上的讲话精神、市委书记郑俊康在市总工会第十七次代表大会开幕式上的讲话精神、市委副书记杜伟在市总工会第十七次代表大会闭幕式上的讲话精神；传达学习市委常委、秘书长、市直机关工委书记黄涛在</span><span class="s1">2015</span><span class="s2">市直机关党的工作会议上的讲话精神及市直机关工委常务副书记陈芳萍在会上所作的《把握机遇</span> <span class="s2">开拓创新</span> <span class="s2">扎实推进机关党建工作新跨越》工作报告。培训期间，组织机关工会干部开展了健步走、气排球比赛、联欢晚会等活动，参观了上汽通用五菱宝骏基地车间。</span></p>','2017/07/03 12:16:22','0';
