insert into [tbl_ArticleInfo]([ImportID],[Title],[ImportCatalog],[Note],[ImportPublicDate],[SerialNum]) select '306','关于表彰市直机关模范职工之家、优秀工会工作者、优秀工会积极分子的通报','公告','<p class="p2" style="text-align: center;"><span class="s1">柳</span> <span class="s1">州</span> <span class="s1">市</span> <span class="s1">直</span> <span class="s1">属</span> <span class="s1">机</span> <span class="s1">关</span></p><p class="p3" style="text-align: center;"><span class="s3">工</span><span class="s1"><span class="Apple-converted-space">  </span></span><span class="s3">会</span><span class="s1"><span class="Apple-converted-space">  </span></span><span class="s3">工</span><span class="s1"><span class="Apple-converted-space">  </span></span><span class="s3">作</span><span class="s1"><span class="Apple-converted-space">  </span></span><span class="s3">委</span><span class="s1"><span class="Apple-converted-space">  </span></span><span class="s3">员</span><span class="s1"><span class="Apple-converted-space">  </span></span><span class="s3">会（</span><span class="s4">通报</span><span class="s3">）</span></p><p class="p4" style="text-align: center;"><span class="s1">柳直工会字〔</span><span class="s5">2016</span><span class="s1">〕</span><span class="s5">9</span><span class="s1">号</span></p><p class="p7" style="text-align: center;"><span class="s1">关于表彰市直机关模范职工之家、</span><span class="s1">优秀工会工作者、优秀工会积极分子的通报</span></p><p class="p9"><span class="s1">市直机关各基层工会：</span></p><p class="p10"><span class="s1">市直机关各基层工会组织在党的领导下，认真践行中国特色社会主义发展道路，牢牢把握为实现中华民族伟大复兴的中国梦而奋斗的当代工人运动时代主题，团结动员广大机关干部职工立足岗位、建功立业，竭诚为干部职工办实事做好事，涌现出了一大批深受机关干部职工信赖和称赞的工会先进集体和优秀工会工作者。为表彰先进，树立典型，进一步深化机关</span><span class="s7">“</span><span class="s1">模范职工之家</span><span class="s7">”</span><span class="s1">建设，市直机关工会工委决定，授予市委办公室、市委宣传部等</span><span class="s7">25</span><span class="s1">个单位</span><span class="s7">“</span><span class="s1">市直机关模范职工之家</span><span class="s7">”</span><span class="s1">荣誉称号；授予雷</span><span class="s7"><span class="Apple-converted-space">  </span></span