insert into [tbl_ArticleInfo]([ImportID],[Title],[ImportCatalog],[Note],[ImportPublicDate],[SerialNum]) select '304','关于柳州市地方税务局工会荣获“全国模范职工之家”的表彰通报','公告','<p class="p1"><span class="s1">柳</span> <span class="s1">州</span> <span class="s1">市</span> <span class="s1">直</span> <span class="s1">属</span> <span class="s1">机</span> <span class="s1">关</span></p><p class="p2"><span class="s3">工</span><span class="s1"><span class="Apple-converted-space">  </span></span><span class="s3">会</span><span class="s1"><span class="Apple-converted-space">  </span></span><span class="s3">工</span><span class="s1"><span class="Apple-converted-space">  </span></span><span class="s3">作</span><span class="s1"><span class="Apple-converted-space">  </span></span><span class="s3">委</span><span class="s1"><span class="Apple-converted-space">  </span></span><span class="s3">员</span><span class="s1"> <span class="Apple-converted-space">  </span></span><span class="s3">会（</span><span class="s4">通报</span><span class="s3">）</span></p><p class="p4"><span class="s1">柳直工会字〔</span><span class="s5">2016</span><span class="s1">〕</span><span class="s5">3</span><span class="s1">号</span></p><p class="p7"><span class="s1">关于柳州市地方税务局工会</span><span class="s1">荣获</span><span class="s2">“</span><span class="s1">全国模范职工之家</span><span class="s2">”</span><span class="s1">的表彰通报</span></p><p class="p9"><span class="s1">市直机关各基层工会：</span></p><p class="p10"><span class="s1">根据《中华全国总工会关于表彰全国模范职工之家、全国模范职工小家、全国优秀工会工作者的决定》（总工发〔</span><span class="s7">2015</span><span class="s1">〕</span><span class="s7">38</span><span class="s1">号）文件精神，柳州市地方税务局工会被中华全国总工会授予</span><span class="s7">“</span><span class="s1">全国模范职工之家</span><span class="s7">”</span><span class="s1">称号，为市直机关工会工作争得了可喜荣誉。希望柳州市地方税务局工会要珍惜荣誉、再接再厉，充分发挥</span><span class="s7">“</span><span class="s1">全国模范职工之家</span><span class="s7">”</span><span class="s1">示范引领作用，在基层工会建设中再创佳绩。市直机关各基层工会组织要以先进为榜样，认真落实党的十八大和十八届三中、四中、五中全会精神，紧紧围绕</span><span class="s7">“</span><span class="s1">四个全